})
export class ProductsComponent implements OnInit {
  constructor() {}

ngOnInit(): void {

}
products = [
  {id: 1, name: 'Minimalists Analog Watch', price: '109', color: 'Black', available: 'Available', image: '/assets/image1.jpg'},
